      <div>
        <h1 className="text-center">Photo List</h1>
        <Link className='btn btn-primary' to="/">Logout</Link>
        <Link className='btn btn-primary' to="/addPhoto">Add Photo</Link>
        <input type="text" onChange={(e) => this.setState({ search: e.target.value.split(' ') })} />

        {/* <input type="file" name="file"></input> */}
        <table className='table table-striped'>
          <thead>
            <tr>
              <td>Photo ID</td>
              <td>Photo name</td>
              <td>Photo</td>
            </tr>
          </thead>
          <tbody>
            {
              this.state.photos.map(
                photo =>
                <tr key = {photo.id}>
                    <td> {photo.id} </td>
                    <td> {photo.name} </td>
                    <td> <img src={photo.data}/> </td>
                </tr>
              )
            }
          </tbody>
        </table>
      </div>
    )
  }
}

export default Dashboard;
